<template>
  <van-tabs v-model:active="bodyType" shrink sticky style="width: 100%; height: 10rem;">
    <van-tab title="JSON">
      <vue-ace-editor style="width: 100%;" :max-lines="22" :data="JSON.stringify(body, null, '\t')" />
    </van-tab>
    <van-tab title="Form">
      <van-row style="height:calc(10rem - 50px); overflow-y: auto;">
        <van-field v-for="item in formBody" readonly :label="item.key">
          <template #input>
            <span v-if="item.type == 'text/plain'">
              {{ item.value }}
            </span>
            <vue-ace-editor v-else-if="item.type == 'application/json'" style="width: 100%;" :max-lines="22"
              :data="JSON.stringify(item.value, null, '\t')" />
            <span v-else-if="/image.*/.test(item.type)">
              <van-image width="50" height="50" :src="item.value" />
            </span>
          </template>
        </van-field>
      </van-row>
    </van-tab>
  </van-tabs>
</template>
<script lang="ts" setup>
import { onMounted, ref } from 'vue'
import VueAceEditor from '../components/VueAceEditor.vue'

interface FormField {
  type: string | 'application/json' | 'text/plain' | 'image/jpeg' | 'image/png' | 'image/gif' | 'image/webp'
  key: string
  value: any
}

const imgBase64 = ''

const types = ['JSON', 'XML', 'Text', 'Form', 'Form-encode', 'graphQL', 'Binary']
const bodyType = ref(0)

const body = {
  username: 'chris',
  email: 'lynx.chrisliu@live.com',
  address: 'shanghai china',
  addresq: 'shanghai china',
  addres3: 'shanghai china',
  addres5: 'shanghai china'
}

const data = defineModel<FormData>('data')
const form: FormData = new FormData()
const formBody = ref<Array<FormField>>([])

onMounted(async () => {
  form.append('username', 'helloworld')
  let blob = new Blob([JSON.stringify(body)], { type: 'application/json' })
  form.append('userInfo', blob)

  let txtBlob = new Blob(['haha nihao ya'], { type: 'text/plain' })
  form.append('plainTxt', txtBlob)

  let arrBuf = new Uint8Array(imgBase64.length)
  for (let i = 0; i < imgBase64.length; ++i) arrBuf[i] = imgBase64.charCodeAt(i)
  let imgBlob = new Blob([imgBase64], { type: 'image/jpeg' })
  form.append('avatar', imgBlob)
  if (data.value) parseForm(data.value)
})

async function parseForm(form: FormData) {
  for (const key of (form as any).keys()) {
    let data = form.get(key)
    if (typeof data == 'string') {
      formBody.value.push({ type: 'text/plain', key, value: data })
    } else {
      let rawData: any
      switch (data.type) {
        case 'application/json':
          rawData = await data.text()
          formBody.value.push({
            type: data.type, key,
            value: JSON.parse(rawData)
          })
          break
        case 'text/plain':
          rawData = await data.text()
          formBody.value.push({
            type: data.type, key,
            value: rawData
          })
          break
        case 'image/jpeg':
        case 'image/png':
        case 'image/gif':
        case 'image/webp':
          formBody.value.push({
            type: data.type, key,
            value: URL.createObjectURL(data)
          })
          break
        case 'application/octet-stream':
          formBody.value.push({
            type: data.type, key,
            value: URL.createObjectURL(data)
          })
          break
      }
    }
  }
}

</script>
<style lang="css"></style>